    private void ptf_signUp_passKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_ptf_signUp_passKeyTyped

         String password = ptf_signUp_pass.getText();
         System.out.println(password);
        if(password.matches("(\\s)*")){
            suggestion.setText("EMPTY SPACES");
            suggestion.setForeground(Color.red);
            result.setText("");
        }
        else if(password.matches("(.)*(\\s)+(.)*")){
            suggestion.setText("Whitespace not allowed");
            suggestion.setForeground(Color.red);
            result.setText("");
        }
        else if(password.length()<8){
            result.setText("WEAK!");
            result.setForeground(Color.red);
            suggestion.setText("Add more characters");
            suggestion.setForeground(Color.red);
        }
        else if(password.length()>30){
            result.setText("Password too long");
            result.setForeground(Color.red);
            suggestion.setText("Reduce 8-30 characters");
            suggestion.setForeground(Color.red);
        }
        else{
            boolean hasNumber = password.matches("(.)*(\\d)(.)*");
            if(!hasNumber){
                result.setText("WEAK!");
                result.setForeground(Color.red);
                suggestion.setText("Add any digit");
                suggestion.setForeground(Color.red);
            }
            else{
                boolean hasSymbol = password.matches("(.)*[\\*\\!\\@\\#\\$\\%\\^\\&\\_\\-\\+\\=\\.\\'\\~\\,\\(\\)\\:\\;\\<\\>\\[\\]\\|\\}\\{\\/]+(.)*");
                if(hasSymbol){
                    result.setText("STRONG");
                    result.setForeground(Color.white);
                    suggestion.setText("");
                }
                else{
                    result.setText("WEAK!");
                    result.setForeground(Color.red);
                    suggestion.setText("Add any symbol");
                }
            }
        }
    }//GEN-LAST:event_ptf_signUp_passKeyTyped

