    boolean forgstrongpass;
    private void ptf_signUp_pass1KeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_ptf_signUp_pass1KeyReleased

        strpass = ptf_signUp_pass1.getText();
        String password = ptf_signUp_pass1.getText();
         System.out.println(password);
        if(password.matches("(\\s)*")){
            sujj.setText("EMPTY SPACES");
            sujj.setForeground(Color.red);
            err.setText("");
        }
        else if(password.matches("(.)*(\\s)+(.)*")){
            sujj.setText("Whitespace not allowed");
            sujj.setForeground(Color.red);
            err.setText("");
        }
        else if(password.length()<8){
            err.setText("Short!");
            err.setForeground(Color.MAGENTA);
            sujj.setText("Add more characters");
            sujj.setForeground(Color.red);
        }
        else if(password.length()>30){
            err.setText("Password too long");
            err.setForeground(Color.red);
            sujj.setText("Reduce 8-30 characters");
            sujj.setForeground(Color.red);
        }
        else{
            boolean hasNumber = password.matches("(.)*(\\d)(.)*");
            if(!hasNumber){
                err.setText("WEAK!");
                err.setForeground(Color.red);
                sujj.setText("Add any digit");
                sujj.setForeground(Color.red);
            }
            else{
                boolean hasSymbol = password.matches("(.)*[\\*\\!\\@\\#\\$\\%\\^\\&\\_\\-\\+\\=\\.\\'\\~\\,\\(\\)\\:\\;\\<\\>\\[\\]\\|\\}\\{\\/]+(.)*");
                if(hasSymbol){
                    forgstrongpass=true;
                    err.setText("STRONG");
                    err.setForeground(Color.white);
                    sujj.setText("");
                }
                else{
                    err.setText("WEAK!");
                    err.setForeground(Color.red);
                    sujj.setText("Add any symbol");
                }
            }
        }
        
    }//GEN-LAST:event_ptf_signUp_pass1KeyReleased

